// Pure-JS SHA256 + HMAC-SHA256 to verify Postman signature
// signature = HMAC_SHA256(timestamp + "\n" + sha256(rawBody))

function sha256(ascii) {
  const rightRotate = (x, n) => (x >>> n) | (x << (32 - n));
  let hash = sha256.h || [];
  let k = sha256.k || [];
  if (!hash.length) {
    let primeCounter = 0, candidate = 2;
    const isComposite = {};
    while (primeCounter < 64) {
      if (!isComposite[candidate]) {
        for (let i = candidate * candidate; i < 312; i += candidate) isComposite[i] = true;
        hash[primeCounter] = ((Math.pow(candidate, 0.5) * Math.pow(2, 32)) | 0) >>> 0;
        k[primeCounter++] = ((Math.pow(candidate, 1 / 3) * Math.pow(2, 32)) | 0) >>> 0;
      }
      candidate++;
    }
    sha256.h = hash; sha256.k = k;
  }
  let words = [], i, j;
  const asciiBitLength = ascii.length * 8;
  ascii += '\x80';
  while (ascii.length % 64 - 56) ascii += '\x00';
  for (i = 0; i < ascii.length; i++) {
    j = ascii.charCodeAt(i);
    words[i >> 2] |= j << ((3 - (i % 4)) * 8);
  }
  words[words.length] = (asciiBitLength / Math.pow(2, 32)) | 0;
  words[words.length] = asciiBitLength;
  let w = [], a = hash[0], b = hash[1], c = hash[2], d = hash[3], e = hash[4], f = hash[5], g = hash[6], h = hash[7];
  for (i = 0; i < words.length; ) {
    let oldA = a, oldB = b, oldC = c, oldD = d, oldE = e, oldF = f, oldG = g, oldH = h;
    for (j = 0; j < 64; j++) {
      w[j] = j < 16 ? words[i + j] : (w[j - 2] >>> 17 ^ w[j - 2] << 15 ^ w[j - 2] >>> 19 ^ w[j - 2] << 13 ^ w[j - 2] >>> 10) + w[j - 7] + (w[j - 15] >>> 7 ^ w[j - 15] << 25 ^ w[j - 15] >>> 18 ^ w[j - 15] << 14 ^ w[j - 15] >>> 3) + w[j - 16] >>> 0;
      const t1 = (h + (e >>> 6 ^ e << 26 ^ e >>> 11 ^ e << 21 ^ e >>> 25 ^ e << 7) + ((e & f) ^ (~e & g)) + k[j] + w[j]) >>> 0;
      const t2 = ((a >>> 2 ^ a << 30 ^ a >>> 13 ^ a << 19 ^ a >>> 22 ^ a << 10) + ((a & b) ^ (a & c) ^ (b & c))) >>> 0;
      h = g; g = f; f = e; e = (d + t1) >>> 0; d = c; c = b; b = a; a = (t1 + t2) >>> 0;
    }
    a = (a + oldA) >>> 0; b = (b + oldB) >>> 0; c = (c + oldC) >>> 0; d = (d + oldD) >>> 0;
    e = (e + oldE) >>> 0; f = (f + oldF) >>> 0; g = (g + oldG) >>> 0; h = (h + oldH) >>> 0;
    i += 16;
  }
  return [a,b,c,d,e,f,g,h].map(x => ('00000000' + x.toString(16)).slice(-8)).join('');
}

function toLatin1(str) { // match Postman/CryptoJS when key is string
  let s = '';
  for (let i = 0; i < str.length; i++) s += String.fromCharCode(str.charCodeAt(i) & 0xff);
  return s;
}

function hexToLatin1(hex) {
  let s = '';
  for (let i = 0; i < hex.length; i += 2) s += String.fromCharCode(parseInt(hex.substr(i, 2), 16));
  return s;
}

function hmacSha256(keyStr, dataStr) {
  const block = 64;
  // key is treated as Latin1 (same as CryptoJS when key is a string)
  let key = toLatin1(keyStr);
  if (key.length > block) key = hexToLatin1(sha256(key));
  if (key.length < block) key = key + '\x00'.repeat(block - key.length);

  let oKeyPad = '', iKeyPad = '';
  for (let i = 0; i < block; i++) {
    const c = key.charCodeAt(i);
    oKeyPad += String.fromCharCode(c ^ 0x5c);
    iKeyPad += String.fromCharCode(c ^ 0x36);
  }
  const inner = sha256(iKeyPad + dataStr);
  const finalHex = sha256(oKeyPad + hexToLatin1(inner));
  return finalHex;
}

function getHeaderCI(hdrs, name) {
  if (!hdrs) return '';
  const key = Object.keys(hdrs).find(k => k.toLowerCase() === name.toLowerCase());
  return key ? String(hdrs[key]) : '';
}

// read from webhook JSON
const headers = $json.headers || {};
const ts = getHeaderCI(headers, 'X-PromptPay-Timestamp');
const sig = getHeaderCI(headers, 'X-PromptPay-Signature');
const rawBody = $json.rawBody || '';

const secret = ($item(0).$node['Set Secret'].json.secret || '');
const bodyHash = sha256(rawBody);           // hex
const data = ts + '\n' + bodyHash;          // ascii-safe
const expected = hmacSha256(secret, data);  // hex

let parsed = {};
try { parsed = rawBody ? JSON.parse(rawBody) : {}; } catch(e) {}

return [{
  json: {
    headers,
    body: parsed,
    auth_ok: typeof sig === 'string' && sig.toLowerCase() === expected.toLowerCase()
  }
}];

// Map input, generate session + amount variant
function randHex(n){ const chars='abcdef0123456789'; let s=''; for(let i=0;i<n;i++){ s+=chars[Math.floor(Math.random()*chars.length)]; } return s; }
function hash32(s){ let h=2166136261>>>0; for(let i=0;i<s.length;i++){ h^=s.charCodeAt(i); h=Math.imul(h,16777619); } return h>>>0; }

// Robust parse: body may be string (from rawBody) or object (from Verify HMAC)
let body = {};
if (typeof $json.body === 'object' && $json.body !== null) {
  body = $json.body;
} else if (typeof $json.body === 'string') {
  try { body = JSON.parse($json.body); } catch(e) { body = {}; }
} else if (typeof $json.rawBody === 'string') {
  try { body = JSON.parse($json.rawBody); } catch(e) { body = {}; }
}

const order = body.order || {};
const cfg   = $item(0).$node['Set PromptPay Config'].json || {};

const order_id   = body.order_id ?? order.id ?? null;
const amount_in  = Number(body.amount ?? order.total ?? 0);
const currency   = String(body.currency ?? order.currency ?? cfg.currency ?? 'THB');
const ttl        = Number(cfg.ttl_sec ?? 600);

let session_token = body.session_token ? String(body.session_token) : randHex(16);

const now = Math.floor(Date.now()/1000);
const expires_epoch = now + ttl;

const use_unique = Boolean(cfg.use_unique_cents);
const mod        = Number(cfg.unique_cents_mod ?? 0);

let amount = Number.isFinite(amount_in) ? Number(amount_in.toFixed(2)) : 0;
let amount_variant = amount;

if (use_unique && mod > 0){
  const h = hash32(session_token) % mod;
  const cents = h % 100;
  const baht = Math.floor(amount);
  amount_variant = Number((baht + (cents/100)).toFixed(2));
}

return [{ json: { order_id, amount, amount_variant, currency, session_token, ttl, expires_epoch } }];

// Build EMV PromptPay payload with CRC16-CCITT
function tlv(id, val){ const len = String(val.length).padStart(2,'0'); return id + len + val; }
function normalizeMobileThailand(v){ let d=String(v||'').replace(/[^0-9]/g,''); if (d.startsWith('0066')) return d; if (d.startsWith('66')) return '0066' + d.slice(2); if (d.startsWith('0')) return '0066' + d.slice(1); return '0066' + d; }
function crc16ccitt(s){ let crc=0xFFFF; for (let i=0;i<s.length;i++){ crc ^= (s.charCodeAt(i) << 8); for (let j=0;j<8;j++){ if (crc & 0x8000){ crc = ((crc << 1) ^ 0x1021) & 0xFFFF; } else { crc = (crc << 1) & 0xFFFF; } } } return crc.toString(16).toUpperCase().padStart(4,'0'); }
const cfg = $item(0).$node['Set PromptPay Config'].json; const pp_type = String(cfg.pp_type||'mobile'); const pp_value_raw = String(cfg.pp_value||''); const merchant_name = String(cfg.merchant_name||'MERCHANT'); const merchant_city = String(cfg.merchant_city||'BANGKOK'); const currency = String($json.currency||cfg.currency||'THB'); const amount = Number($json.amount_variant||$json.amount||0).toFixed(2); const poi = '12'; // dynamic QR for fixed amount
const maiAID = tlv('00','A000000677010111'); let acctVal='', acctTag='01'; if (pp_type==='mobile'){ acctVal = normalizeMobileThailand(pp_value_raw); acctTag='01'; } else { acctVal = pp_value_raw.replace(/[^0-9]/g,''); acctTag='02'; } const mai = tlv('29', maiAID + tlv(acctTag, acctVal)); const mcc = tlv('52','0000'); const curr = tlv('53','764'); const amt = tlv('54', String(amount)); const country = tlv('58','TH'); const mname = tlv('59', merchant_name.substring(0,25)); const mcity = tlv('60', merchant_city.substring(0,15)); const header = tlv('00','01') + tlv('01', poi); const toCRC = header + mai + mcc + curr + amt + country + mname + mcity + '6304'; const crc = crc16ccitt(toCRC); const emv = toCRC + crc; return [{ json: Object.assign({}, $json, { emv }) }];
